import React, { useEffect, useState } from 'react';

function App() {
  const [data, setData] = useState(null);

  useEffect(() => {
    fetch('/api/message')
      .then(res => res.json())
      .then(json => setData(json))
      .catch(err => console.log("Erreur de connexion au backend"));
  }, []);

  if (!data) return <h2 style={{ textAlign: 'center', marginTop: '50px' }}>‚è≥ Connexion au Cluster en cours...</h2>;

  return (
    <div style={{ padding: '40px', fontFamily: 'sans-serif', backgroundColor: '#f4f7f6', minHeight: '100vh' }}>
      <div style={{ maxWidth: '600px', margin: 'auto', backgroundColor: 'white', padding: '20px', borderRadius: '10px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)' }}>
        <h1 style={{ color: '#2c3e50', borderBottom: '2px solid #3498db', paddingBottom: '10px' }}>
          Dashboard Kubernetes
        </h1>
        <p><strong>Statut Backend :</strong> ‚úÖ {data.message}</p>
        <p><strong>Statut BDD :</strong> üü¢ {data.database}</p>

        <h3 style={{ marginTop: '30px' }}>Donn√©es r√©cup√©r√©es en Base :</h3>
        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr style={{ backgroundColor: '#ecf0f1' }}>
              <th style={{ padding: '10px', textAlign: 'left' }}>Nom</th>
              <th style={{ padding: '10px', textAlign: 'left' }}>R√¥le</th>
              <th style={{ padding: '10px', textAlign: 'left' }}>Statut</th>
            </tr>
          </thead>
          <tbody>
            {data.items.map(item => (
              <tr key={item.id} style={{ borderBottom: '1px solid #eee' }}>
                <td style={{ padding: '10px' }}>{item.nom}</td>
                <td style={{ padding: '10px' }}>{item.role}</td>
                <td style={{ padding: '10px',  }}>{item.statut}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
